<?php/*  * To change this license header, choose License Headers in Project Properties. * To change this template file, choose Tools | Templates * and open the template in the editor. */class Meeting_model extends CI_Model {	public function save_meeting($data){		$meeting_date = date('Y-m-d', strtotime($data['meeting_date']));		$meeting_data = array(			'meeting_place'=>$data['meeting_city'],			'meeting_type'=>$data['meeting_type'],			'remark'=>$data['remark'],			'meeting_date'=>$meeting_date,			'crm_user_id'=> logged_user_data(),			'created_date'=>savedate(),                  			'updated_date'=>savedate(),                  			'status'=>1, 		);		$this->db->insert('user_meeting',$meeting_data); 		return ($this->db->affected_rows() == 1) ? true : false; 		    }	public function get_meeting_list(){		$arr = "meeting_id,crm_user_id,meeting_date,meeting_place,remark ,meeting_type,status";        $this->db->select($arr);        $this->db->from("user_meeting");        $this->db->where('status',1); 		if(!is_admin()){	        $this->db->where('crm_user_id',logged_user_data());	    }        $query = $this->db->get();        if($this->db->affected_rows()){			return $query->result_array();		}        else{            return FALSE;        }	}		public function get_meeting_master(){		$arr = "meeting_id,meeting_value";		$this->db->select($arr);		$this->db->from("user_meeting_master");		$this->db->where('status',1);		$query = $this->db->get();		if($this->db->affected_rows()){			return $query->result_array();		}		else{			return FALSE;		}	}		public function get_meeting_data($id){		$col='meeting_id,crm_user_id,meeting_date,meeting_place,remark ,meeting_type,status';		$this->db->select($col); 		$this->db->from('user_meeting'); 		$this->db->where('meeting_id',$id); 		$query= $this->db->get(); 		if($this->db->affected_rows()){			return $query->result_array(); 		}			else{            return FALSE;        }    }		public function update_meeting($data){		$meeting_date = date('Y-m-d', strtotime($data['meeting_date']));		$meeting_data = array(			'meeting_place'=>$data['meeting_city'],			'meeting_type'=>$data['meeting_type'],			'remark'=>$data['remark'],			'meeting_date'=>$meeting_date,			'updated_date'=>savedate(),                  		);		$this->db->set($meeting_data);		$this->db->where('meeting_id',$data['meeting_id']); 		$this->db->update('user_meeting'); 		return ($this->db->affected_rows() == 1) ? true : false;     }		}?>